import confort_matoti from './images/conf.JPG'

let data_info = {
    about: {
        missionB:{statement:'The Foundation aspires to achieve this through continuous engagement initiatives and structured mentorship and coaching interventions.',quote:'"mentorship and coaching"'},
        missionE:{statement:'The Foundation aspires to rebuild, plant hope, offer relevant support to those who have already fallen victim of the social ails and proactively alleviate social pressures to those who have not yet been exposed to the current challenges affecting a boy child.',quote:'"alleviate challenges affecting a boy child."'},
    },

    who: [
        {mission:'Mandla Radebe Foundation is a Non-Profit organization founded in 2017. Its existence is grounded on the premise of rebuilding, empowering and supporting the youth with a niche on the boy child.'},
        {mission:'The Foundation aspires to achieve this through continuous engagement initiatives and structured mentorship and coaching interventions.'},
        {mission: 'The social dynamics of the 21st century demands a unique approach in dealing with a boy child.  The prevalence and the intrusion of the social media, its effects and lack of management thereof; the acute rise of substance abuse, the lack of educational opportunities and financial support post grade 12, etc. has impacted the youth and had dire impact to a boy child.'},
        {mission:'The foundation has identified these and other social dynamics which influences why a boy child would often find himself neglected and being misunderstood by his nucleus family and the society at large.'},
        {mission:'The Foundation aspires to rebuild, plant hope, offer relevant support to those who have already fallen victim of the social ails and proactively alleviate social pressures to those who have not yet been exposed to the current challenges affecting a boy child.'},
        {mission: 'As great as the Girl Child development programs are, the foundation has noted with great concern, the negligence of a Boy Child, thus shifting the gender inequality, rather than bringing about equality on the Boy and Girl Child.'}
    ],

    what_we_are: {
        img:'https://source.unsplash.com/200x201',
        mission:['We are an organization driven by the urge and necessity of ensuring that a Boy Child can rebrand back to communities. We conduct programs which keep a boy child engaged academically, socially and spiritually; facilitate community driven initiatives with an objective to keep a boy child involved with continuous improvements in their respective communities and become the change that is much needed; provide motivation and train a boy child to instill motivation to his peers; encourage and boost the confidence of a Boy Child. ',
        'Boy children are often affected by many issues, many of which remain undisclosed.  The Foundation desires provide a solution by being the ears and a pulse for such issues.  Moreover, the foundation offers counselling, group discussions and consultative programs to create a platform for young Boy children to speak out.',
        'We have a long-term goal of opening an Online School and an Online University. In line with the Fourth Industrial Revolution, advancement of the ICT and Internet of Things, we seek to bridge the gap between the previously disadvantaged and advantaged. Taking advantage of the internet usage by our youth, we seek to utilize online platforms to mobilize and campaign our ideal goal.']
    },

    goals: [
        {goal:'Boy Child Mentorship',points:['Boy Child Award (Academics, Sports, Social Empowerment)',' Entrepreneurial development',' Young Men’s conference','Walk with the Mayor']},
        {goal:'Walking A Lifetime Journey Program',points:['Lifetime Parenting/Adoption','Career building','Rebuilding A Boy Child','Discover Yourself (Talent search program)']},
        {goal:'Education and Leadership',points:['Application drive','Bursary, learnership and internships drive','Annual Leadership Seminar','Leadership Awards']},
        {goal:'Mandla Radebe Foundation National Roadshow',points:['National Roadshow to raise awareness of Mandla Radebe Foundation']},
        {goal:'Consultancy',points:['Counselling','Group discussions']},
        {goal:'Business Activation (Outstanding)',points:['Online School','Online University']},
        {goal:'Business Incubation (Outstanding)',points:['Outstanding']},
        {goal:'Funding',points:['Crowdfunding','Donations','investments']},
